//Write a piece of software that:

//while there is another character in the given string
//reads a character from the given string
//parses the character into an integer
//adds this integer to a sum variable






string input = " \r\n";
int sumofallNumbers = 0;

foreach (char c in input)
{

    if (char.IsDigit(c)) 
    {
        int parsedNumber;
        if (int.TryParse(c.ToString(), out parsedNumber))
        {
            sumofallNumbers += parsedNumber;
        }
    }
}

Console.WriteLine($"The sum of all the numbers is {sumofallNumbers}");